import React from 'react';
import SearchIcon from './search.svg';
import clear_icon from './Assets/clear.png';
import cloud_icon from './Assets/cloud.png';
import drizzle_icon from './Assets/drizzle.png';
import humidity_icon from './Assets/humidity.png';
import rain_icon from './Assets/rain.png';
import snow_icon from './Assets/snow.png';

const Hero = ({ searchTerm, setSearchTerm, searchCity, cityName, temperature, weatherCondition }) => {

    if (data.weather[0].icon === "01d" || data.weather[0].icon === "01n") {
      setWeatherCondition(clear_icon);
      console.log("01d");
    } else if (data.weather[0].icon === "02d" || data.weather[0].icon === "02n") {
      setWeatherCondition(cloud_icon);
      console.log("02d");
    } else if (data.weather[0].icon === "03d" || data.weather[0].icon === "03n") {
      setWeatherCondition(rain_icon);
      console.log("03d");
    }else if (data.weather[0].icon === "04d" || data.weather[0].icon === "04n") {
      setWeatherCondition(rain_icon);
      console.log("04d");
    }else if (data.weather[0].icon === "09d" || data.weather[0].icon === "09n") {
      setWeatherCondition(rain_icon);
      console.log("09d");
    }else if (data.weather[0].icon === "10d" || data.weather[0].icon === "10n") {
      setWeatherCondition(rain_icon);
      console.log("10d");
    }else{
      setWeatherCondition(clear_icon);
      console.log("else");
    }

    return (
        <div className="hero grid place-content-center">
            <h1 className='text-gray-200 text-4xl font-semibold mt-28 mb-8'>Choosen location: <b>{cityName}</b></h1>
            <div className="grid place-content-center">
                <h2 className='text-gray-200 text-6xl font-bold mb-8'>{temperature}Â°C</h2>
            </div>
            <div className="weather-icon flex justify-center mb-6">
                <img src={cloud_icon} alt="" />
            </div>
            <div className="relative text-uppercase pb-32">
                <input
                    className='w-full p-3 pb-3 border-b-4 bg-transparent focus:outline-none text-gray-200 text-3xl font-semibold text-center uppercase'
                    type="text"
                    placeholder='Input City'
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <img
                    className='svg-hover absolute right-0 top-0 bottom-0 bg-transparent border-none px-4 py-3 hover:'
                    src={SearchIcon}
                    alt='search'
                    onClick={() => searchCity(searchTerm)}
                />
            </div>
        </div>
    );
};

export default Hero;
