import { useState, useEffect } from "react";
import Hero from "./Hero.jsx";
import './index.css';
import clear from '../src/assets/clear.mp4';
import cloudy from '../src/assets/cloudy.mp4';
import rainy from '../src/assets/rainy.mp4';

// import './output.css';

const App = () => {

  const URL_API = "http://api.openweathermap.org/data/2.5/weather";
  const API_KEY = "504b61bc03f3cad674cc5a1654127f69";

  const [city, setCity] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [temperature, setTemperature] = useState(null);
  const [videoSrc, setVideoSrc] = useState(null);

  const searchCity = async (cityName) => {
    const response = await fetch(`${URL_API}?q=${cityName}&appid=${API_KEY}`);
    const data = await response.json();
    console.log(data);
    setCity(data);

    if (data.main && data.main.temp) {
      setTemperature(data.main.temp);
    } else {
      setTemperature(null);
    }

    if (city.weather && city.weather.length > 0) {
      const weatherCondition = city.weather[0].main.toLowerCase();
      switch (weatherCondition) {
        case 'clouds':
          setVideoSrc(cloudy);
          break;
        case 'rain':
          setVideoSrc(rainy);
          break;
        case 'clear':
          setVideoSrc(clear);
          break;
        default:
          setVideoSrc(null);
      }
    } else {
      setVideoSrc(null);
    }

  }

  useEffect(() => {
    searchCity("Poznan");
  }, []);

  if (city.weather && city.weather.length > 0) {
    const weatherCondition = city.weather[0].main.toLowerCase();
    switch (weatherCondition) {
      case 'clouds':
        setVideoSrc(cloudy);
        break;
      case 'rain':
        setVideoSrc(rainy);
        break;
      case 'clear':
        setVideoSrc(clear);
        break;
      default:
        setVideoSrc(null);
    }
  } else {
    setVideoSrc(null);
  }

  return (
    <>
      <Hero
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        searchCity={searchCity}
        cityName={city.name}
        temperature={Math.round(temperature - 273.15)}
        videoSrc={videoSrc}
      />
    </>
  );
}

export default App;
