import React from 'react'
import './index.css';
import SearchIcon from './search.svg'

const Hero = ({ searchTerm, setSearchTerm, searchCity, cityName, temperature }) => {
    return (
        <>
            <div className="grid place-content-center bg-indigo-950">
                <h1 className='text-gray-200 text-4xl font-semibold mt-20 mb-8'>Choosen location: <b>{cityName}</b></h1>
                <div className="grid">
                    <h2 className='text-gray-200 text-6xl font-bold mb-8'>{temperature}Â°C</h2>
                </div>
                <div className="flex text-uppercase">
                    <input
                        className='w-full p-3 pb-3 border-b-4 bg-transparent focus:outline-none text-gray-200 text-4xl font-semibold text-center uppercase'
                        type="text"
                        placeholder='Input City'
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <img
                        src={SearchIcon}
                        alt='search'
                        onClick={() => searchCity(searchTerm)}
                    />
                </div>
            </div>
        </>
    )
}

export default Hero;
