import React, { useState, useEffect } from "react";
import Hero from "./Hero.jsx";
import './index.css';

const App = () => {
  const URL_API = "http://api.openweathermap.org/data/2.5/weather";
  const API_KEY = "504b61bc03f3cad674cc5a1654127f69";


  const weatherCondition = {
    "01d": "./clear.mp4",
    "01n": "./clear.mp4",
    "02d": "./cloudy.mp4",
    "02n": "./cloudy.mp4",
    "03d": "./rainy.mp4",
    "03n": "./rainy.mp4",
    "04d": "./rainy.mp4",
    "04n": "./rainy.mp4",
    "09d": "./rainy.mp4",
    "09n": "./rainy.mp4",
    "10d": "./rainy.mp4",
    "10n": "./rainy.mp4",
  };

  const [city, setCity] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [temperature, setTemperature] = useState(null);
  const [weatherIcon, setWeatherIcon] = useState("01d");

  const searchCity = async (cityName) => {
    try {
      const response = await fetch(`${URL_API}?q=${cityName}&appid=${API_KEY}`);
      const data = await response.json();
      console.log(data);
      setCity(data);

      if (data.main && data.main.temp) {
        setTemperature(data.main.temp);
      } else {
        setTemperature(null);
      }

      if (data.weather && data.weather[0] && data.weather[0].icon) {
        setWeatherIcon(data.weather[0].icon);
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    searchCity("Barcelona");
  }, []);

  return (
    <>
      <Hero
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        searchCity={searchCity}
        cityName={city.name}
        temperature={Math.round(temperature - 273.15)}
        weatherCondition={weatherCondition[weatherIcon]}
      />
    </>
  );
}

export default App;
