import React from 'react'
import './index.css';

const Hero = ({ searchTerm, setSearchTerm, searchCity, cityName, temperature }) => {
    return (
        <>
            <div className="grid place-content-center bg-indigo-950">
                <h1 className='text-gray-200 text-4xl font-semibold mt-20 mb-12'>Choosen location: <></>{cityName}</h1>
                <h2 className='text-gray-200'>{temperature}Â°C</h2> 
                <input
                    className=''
                    type="text"
                    placeholder='Input City'
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button 
                alt="search"
                onClick={() => searchCity(searchTerm)}>
                    Search
                </button>
            </div>
        </>
    )
}

export default Hero;
