import React from "react";
import "./index.css";
import SearchIcon from "./search.svg";

const Hero = ({
  searchTerm,
  setSearchTerm,
  searchCity,
  cityName,
  temperature,
  videoSrc
}) => {
  return (
    <div className="hero grid place-content-center">
      {videoSrc && ( // Check if videoSrc is not null before rendering the video
        <video autoPlay muted loop className="hero-video">
          <source src={`/${videoSrc}`} type="video/mp4" />{" "}
          {/* Use videoSrc directly */}
        </video>
      )}
      <div className="content">
        <h1 className="text-gray-200 text-4xl font-semibold mt-28 mb-8">
          Choosen location: <b>{cityName}</b>
        </h1>
        <div className="grid place-content-center">
          <h2 className="text-gray-200 text-6xl font-bold mb-8">
            {temperature}Â°C
          </h2>
        </div>
        <div className="relative text-uppercase pb-32">
          <input
            className="w-full p-3 pb-3 border-b-4 bg-transparent focus:outline-none text-gray-200 text-3xl font-semibold text-center uppercase"
            type="text"
            placeholder="Input City"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <img
            className="svg-hover absolute right-0 top-0 bottom-0 bg-transparent border-none px-4 py-3 hover:"
            src={SearchIcon}
            alt="search"
            onClick={() => searchCity(searchTerm)}
          />
        </div>
      </div>
    </div>
  );
};

export default Hero;
