import { useState, useEffect } from "react";
import Hero from "./Hero.jsx";

const App = () => {

  const URL_API = "http://api.openweathermap.org/data/2.5/weather";
  const API_KEY = "504b61bc03f3cad674cc5a1654127f69";


  const [city, setCity] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [temperature, setTemperature] = useState(null);

  const searchCity = async (cityName) => {
    const response = await fetch(`${URL_API}?q=${cityName}&appid=${API_KEY}`);
    const data = await response.json();
    console.log(data);
    setCity(data);

    if (data.main && data.main.temp) {
      const kelvinTemperature = data.main.temp;
      const celsiusTemperature = kelvinTemperature - 273.15;
      setTemperature(celsiusTemperature.toFixed(2));
    } else {
      setTemperature(null);
    }
  }

  useEffect(() => {
    searchCity("Paris");
  }, [])


  return (
    <>
      <Hero
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        searchCity={searchCity}
        cityName={city.name}
        temperature={temperature - 273.15}
      />
      <div className="bg"></div>
    </>
  );
}

export default App;
