import React, { useState, useEffect } from "react";
import Hero from "./Hero.jsx";
import './index.css';

const App = () => {
  const URL_API = "http://api.openweathermap.org/data/2.5/weather";
  const API_KEY = "504b61bc03f3cad674cc5a1654127f69";

  const [city, setCity] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [temperature, setTemperature] = useState(null);

  const searchCity = async (cityName) => {
    const response = await fetch(`${URL_API}?q=${cityName}&appid=${API_KEY}`);
    const data = await response.json();
    console.log(data);
    setCity(data);

    if (data.main && data.main.temp) {
      setTemperature(data.main.temp);
    } else {
      setTemperature(null);
    }

    if (data.weather[0].icon === "01d" || data.weather[0].icon === "01n") {
      setWeatherCondition(clear_icon);
      console.log("01d");
    } else if (data.weather[0].icon === "02d" || data.weather[0].icon === "02n") {
      setWeatherCondition(cloud_icon);
      console.log("02d");
    } else if (data.weather[0].icon === "03d" || data.weather[0].icon === "03n") {
      setWeatherCondition(rain_icon);
      console.log("03d");
    } else if (data.weather[0].icon === "04d" || data.weather[0].icon === "04n") {
      setWeatherCondition(rain_icon);
      console.log("04d");
    } else if (data.weather[0].icon === "09d" || data.weather[0].icon === "09n") {
      setWeatherCondition(rain_icon);
      console.log("09d");
    } else if (data.weather[0].icon === "10d" || data.weather[0].icon === "10n") {
      setWeatherCondition(rain_icon);
      console.log("10d");
    } else {
      setWeatherCondition(clear_icon);
      console.log("else");
    }

  }


  useEffect(() => {
    searchCity("Barcelona");
  }, []);

  return (
    <>
      <Hero
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        searchCity={searchCity}
        cityName={city.name}
        temperature={Math.round(temperature - 273.15)}
      />
    </>
  );
}

export default App;
